import React, { useState, useEffect } from 'react';
import { signOut } from 'firebase/auth';
import { collection, addDoc, query, where, onSnapshot, updateDoc, doc, deleteDoc } from 'firebase/firestore';
import { auth, db } from '../firebase/firebaseConfig';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameDay, isToday } from 'date-fns';
import { es } from 'date-fns/locale';

function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    if (!auth.currentUser) return;

    const q = query(
      collection(db, 'tasks'),
      where('userId', '==', auth.currentUser.uid)
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const tasksData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        date: doc.data().date?.toDate()
      }));
      setTasks(tasksData);
    });

    return () => unsubscribe();
  }, []);

  const addTask = async (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;

    await addDoc(collection(db, 'tasks'), {
      text: newTask,
      completed: false,
      date: currentDate,
      userId: auth.currentUser.uid,
      createdAt: new Date()
    });

    setNewTask('');
  };

  const toggleTask = async (taskId, completed) => {
    await updateDoc(doc(db, 'tasks', taskId), { completed: !completed });
  };

  const deleteTask = async (taskId) => {
    await deleteDoc(doc(db, 'tasks', taskId));
  };

  const handleLogout = () => signOut(auth);

  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(currentDate);
  const monthDays = eachDayOfInterval({ start: monthStart, end: monthEnd });

  const getTasksForDay = (day) => {
    return tasks.filter(task => task.date && isSameDay(task.date, day));
  };

  return (
    <div className="container" style={{ padding: '2rem 1rem' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <div>
          <h1 style={{ fontSize: '2rem', fontWeight: '300' }}>Arch</h1>
          <p style={{ color: 'var(--text-light)' }}>Tu rutina. Tu mente. Tu orden.</p>
        </div>
        <button onClick={handleLogout} className="btn" style={{ background: 'var(--secondary)', color: 'white' }}>
          Salir
        </button>
      </header>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '2rem', alignItems: 'start' }}>
        <div className="card">
          <h2 style={{ marginBottom: '1rem', fontSize: '1.25rem' }}>Nueva Tarea</h2>
          <form onSubmit={addTask}>
            <div className="form-group">
              <input
                type="text"
                placeholder="¿Qué quieres hacer?"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary" style={{ width: '100%' }}>
              Agregar
            </button>
          </form>

          <div style={{ marginTop: '2rem' }}>
            <h3 style={{ marginBottom: '1rem', fontSize: '1.1rem' }}>Tareas de Hoy</h3>
            {getTasksForDay(new Date()).map(task => (
              <div key={task.id} className={`task ${task.completed ? 'completed' : ''}`}>
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTask(task.id, task.completed)}
                />
                <span style={{ flex: 1 }}>{task.text}</span>
                <button
                  onClick={() => deleteTask(task.id)}
                  style={{ background: 'none', border: 'none', color: 'var(--text-light)', cursor: 'pointer' }}
                >
                  ×
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
            <button
              onClick={() => setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1))}
              className="btn"
              style={{ background: 'none', border: '1px solid var(--border)' }}
            >
              ←
            </button>
            <h2>{format(currentDate, 'MMMM yyyy', { locale: es })}</h2>
            <button
              onClick={() => setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1))}
              className="btn"
              style={{ background: 'none', border: '1px solid var(--border)' }}
            >
              →
            </button>
          </div>

          <div className="calendar">
            {['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb'].map(day => (
              <div key={day} style={{ padding: '0.5rem', textAlign: 'center', fontWeight: '500', background: 'var(--background)' }}>
                {day}
              </div>
            ))}
            {monthDays.map(day => {
              const dayTasks = getTasksForDay(day);
              return (
                <div
                  key={day.toString()}
                  className={`calendar-day ${isToday(day) ? 'today' : ''}`}
                  onClick={() => setCurrentDate(day)}
                  style={{ cursor: 'pointer' }}
                >
                  <div style={{ fontWeight: isToday(day) ? '600' : 'normal' }}>
                    {format(day, 'd')}
                  </div>
                  <div style={{ display: 'flex', flexWrap: 'wrap', marginTop: '0.25rem' }}>
                    {dayTasks.slice(0, 3).map((task, i) => (
                      <div key={i} className="task-dot" title={task.text}></div>
                    ))}
                    {dayTasks.length > 3 && (
                      <div style={{ fontSize: '0.75rem', color: 'var(--text-light)' }}>
                        +{dayTasks.length - 3}
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
